import styled from 'styled-components'

export const SignInContainer = styled.div`
  width: 380px;
  display: flex;
  flex-direction: column;
`
export const TitleContainer = styled.h2`
  margin: 10px 0;
`
export const ButtonBar = styled.div`
  display: flex;
  justify-content: space-between;
`
